import axios from "axios";
import { useState } from "react";

function RegInput() {
  const [regNo, setRegNo] = useState("");
  const [carDetails, setCarDetails] = useState("");

  const handleChangeReg = (event) => {
    setRegNo(event.target.value);
  };

  const handleSearchRegNo = async (regNo) => {
    regNo.preventDefault();
    try {
      const carInfo = await axios.get(
        `https://beta.check-mot.service.gov.uk/trade/vehicles/mot-tests?registration=${regNo}`,
        {
          headers: {
            "x-api-key": "fZi8YcjrZN1cGkQeZP7Uaa4rTxua8HovaswPuIno",
          },
        }
      );
      return setCarDetails(carInfo);
    } catch (e) {
      console.error(e);
    }
  };

  console.log(carDetails);

  return (
    <div>
      <h1>Add Reg Number Here</h1>

      <form onSubmit={handleSearchRegNo}>
        <input id="regno" type="text" onChange={handleChangeReg} />
        <button>Search Reg</button>
      </form>
    </div>
  );
}

export default RegInput;
