import axios from "axios";
import { useState } from "react";

function RegInput() {
  const [regNo, setRegNo] = useState("");
  const [carDetails, setCarDetails] = useState("");

  const handleChangeReg = (event) => {
    setRegNo(event.target.value);
  };

  const handleSearchRegNo = async (event) => {
    event.preventDefault();
    try {
      const carInfo = await axios.get(
        `https://cors-anywhere.herokuapp.com/https://beta.check-mot.service.gov.uk/trade/vehicles/mot-tests?registration=${regNo}`,
        {
          headers: {
            "x-api-key": "fZi8YcjrZN1cGkQeZP7Uaa4rTxua8HovaswPuIno",
          },
        }
      );
      return setCarDetails(carInfo.data[0]);
    } catch (e) {
      console.error(e);
    }
  };

  const resetSearch = () => {
    setCarDetails("");
    setRegNo("");
  };

  return (
    <div>
      <div class="px-8 mt-12">
        <h1 class="font-mono text-3xl mb-4 text-center">MOT CHECKER</h1>
        <h2 class="font-mono text-xl mb-3 text-center">
          Check the MOT status of a vehicle
        </h2>
        <h3 class="font-mono text-sm text-center">
          Find out if a vehicle has an MOT certificate and when it runs out.
        </h3>
        <h3 class="font-mono text-sm mb-6 text-center">
          You’ll need the vehicle’s registration number (number plate).
        </h3>
      </div>
      <div class="px-8 mb-6">
        <form
          onSubmit={handleSearchRegNo}
          class="flex flex-col justify-center items-center"
        >
          <p class="font-mono mt-2 font-medium text-center">
            enter the registration number here
          </p>

          <input
            class="font-mono h-12 my-1 px-3 py-2 bg-white border shadow-sm border-slate-300 placeholder-slate-400 focus:outline-none focus:border-amber-300 focus:ring-sky-500 block w-full rounded-md sm:text-sm focus:ring-1 w-3/5 text-center"
            placeholder="e.g. CU57ABC"
            id="regno"
            value={regNo}
            type="text"
            onChange={handleChangeReg}
          />
          <button class="font-mono border-solid border-2 border-emerald-800 rounded-md p-1 w-2/5 mb-1 ">
            Search Reg
          </button>
        </form>
        <form class="flex flex-col justify-center items-center">
          <button
            class="font-mono border-solid border-2 border-amber-300 rounded-md p-1 w-1/4 "
            onClick={resetSearch}
          >
            Reset
          </button>
        </form>
      </div>
    </div>
  );
}

export default RegInput;
