import CarInfo from "./CarInfo";
import axios from "axios";
import { useState } from "react";

function RegInput() {
  const [regNo, setRegNo] = useState("");
  const [carDetails, setCarDetails] = useState("");

  const handleChangeReg = (event) => {
    setRegNo(event.target.value);
  };

  const handleSearchRegNo = async (event) => {
    event.preventDefault();
    try {
      const carInfo = await axios.get(
        `https://cors-anywhere.herokuapp.com/https://beta.check-mot.service.gov.uk/trade/vehicles/mot-tests?registration=${regNo}`,
        {
          headers: {
            "x-api-key": "fZi8YcjrZN1cGkQeZP7Uaa4rTxua8HovaswPuIno",
          },
        }
      );
      return setCarDetails(carInfo.data[0]);
    } catch (e) {
      console.error(e);
    }
  };

  const resetSearch = () => {
    setCarDetails("");
    setRegNo("");
  };

  return (
    <div class="max-w-md mx-auto bg-white rounded-xl shadow-md overflow-hidden md:max-w-2xl">
      {/* <div class="max-w-md mx-auto bg-white rounded-xl shadow-md overflow-hidden md:max-w-2xl">
  <div class="md:flex">
    <div class="md:shrink-0">
      <img class="h-48 w-full object-cover md:h-full md:w-48" src="/img/store.jpg" alt="Man looking at item at a store">
    </div>
    <div class="p-8">
      <div class="uppercase tracking-wide text-sm text-indigo-500 font-semibold">Case study</div>
      <a href="#" class="block mt-1 text-lg leading-tight font-medium text-black hover:underline">Finding customers for your new business</a>
      <p class="mt-2 text-slate-500">Getting a new business off the ground is a lot of hard work. Here are five ideas you can use to find your first customers.</p>
    </div>
  </div>
</div> */}
      <div class="md:flex">
        <div class="md:shrink-0">
          <div class="px-8 mt-12">
            <h1 class="text-3xl mb-6 ">MOT CHECKER</h1>
            <h2 class="text-2xl mb-6">Check the MOT status of a vehicle</h2>
            <h3>
              Find out if a vehicle has an MOT certificate and when it runs out.
            </h3>
            <h3 class="mb-6">
              You’ll need the vehicle’s registration number (number plate).
            </h3>
          </div>
          <div class="px-8 mb-6">
            <form onSubmit={handleSearchRegNo}>
              <p class="mt-2 text-slate-500">
                enter the registration number here
              </p>

              <input
                class="mt-1 px-3 py-2 bg-white border shadow-sm border-slate-300 placeholder-slate-400 focus:outline-none focus:border-sky-500 focus:ring-sky-500 block w-full rounded-md sm:text-sm focus:ring-1"
                placeholder="CU57ABC"
                id="regno"
                value={regNo}
                type="text"
                onChange={handleChangeReg}
              />
              <button>Search Reg</button>
            </form>
            <button
              class="border-solid border-2 border-emerald-800 rounded"
              onClick={resetSearch}
            >
              Reset
            </button>
          </div>
          <div class="px-8">
            <CarInfo carDetails={carDetails} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default RegInput;
